       if (d.getElementById(id)) {return;}
       js = d.createElement('script'); js.id = id; js.async = true;
       js.src = "http://connect.facebook.net/en_US/all.js";
       ref.parentNode.insertBefore(js, ref);
      }(document));
      function testAPI() {
       console.log('Welcome!  Fetching your information.... ');
       FB.api('/me', function(response) {
        console.log('Good to see you, ' + response.name + '.');
        console.log(response);
       });
      }
      window.fbAsyncInit = function()
      {
        FB.init({
          appId      : '561885653907144',
          status     : true, // check login status
          cookie     : true, // enable cookies to allow the server to access the session
          xfbml      : true  // parse XFBML
        });
        console.log(FB);return;
        // Here we subscribe to the auth.authResponseChange JavaScript event. This event is fired
        // for any authentication related change, such as login, logout or session refresh. This means that
        // whenever someone who was previously logged out tries to log in again, the correct case below 
        // will be handled.
        FB.Event.subscribe('auth.authResponseChange', function(response) {         
          FB.api('/me', function(response) {
            $.ajax({
                type:"post",
                url:"<?php echo base_url(); ?>spiffcity/home/fb_login_js",
                data: response,
                success:function(retdata){
                  if(retdata["success"] == true){
                    window.location.href ="<?php echo base_url(); ?>spiffcity/popular";
                  }
                }
            });
          });                     
        });
        
        $(document).on("click",".connect-facebook",function(){
          FB.api('/me', function(response){
            if(typeof response.error != "undefined"){
              FB.login(function(loginResponse)
              {
                if(loginResponse.authResponse != null && loginResponse.status == "connected"){
                  var loginData = {};
                  loginData['accessToken'] = loginResponse.authResponse.accessToken;
                  loginData['expiresIn'] = loginResponse.authResponse.expiresIn;
                  loginData['userID'] = loginResponse.authResponse.userID;
                  FB.api('/me', function(response){
                    $.ajax({
                      type:"post",
                      url:"<?php echo base_url(); ?>spiffcity/home/fb_login_js",
                      data: response,
                      success:function(retdata){
                        if(retdata["success"] == true){
                          window.location.href ="<?php echo base_url(); ?>spiffcity/popular";
                        }
                      }
                    });
                  });
                }
                else{
                  
                }
              });
            }
          });
        });
      }
      
