  <script type="text/javascript">
     $(document).ready(function(){
        //to validate the signup form input fields !! 
        $("#frmRegistration").validationEngine('attach',{
           binded: false
         });
        $("#frmSignin").validationEngine('attach',{
           binded: false
         });      
        // to show email availability
        $("#email").blur (function(){
              if ($("#email").val().length>5) {
                    $("#user_email").html("font color='GREEN'>Checking</font>");
                    $.ajax({
                          type:"POST",
                          url:"<?php echo base_url(); ?>spiffcity/home/check_email_availability",
                          data:"user_email="+$("#email").val(),
                          success:function(msg){
                                if (msg['success'] == true) {
                                      $("#user_email").html("<font color='GREEN'>Available</font>");
                                }else{
                                      $("#user_email").html("<font color='RED'>Already Taken</font>");
                                }
                          }
                    })
              }
        });
        // to show username availability 
        $("#username").keyup(function(){
         if($("#username").val().length <4){
           $("#username_result").html("<font color='RED'>Minimuim amount of chars is 4.</font>");
         }
         else{
           $("#username_result").html("<font color='GREEN'>Checking...");
           $.ajax({
              type:"POST",
              url :"<?php echo base_url(); ?>spiffcity/home/check_username_availability",
              data:"user="+$("#username").val(),
              success: function(msg){
              if (msg['success'] == true) {
               $("#username_result").html("<font color='GREEN'>Available.</font");
              }else{
               $("#username_result").html("<font color='RED'>Already Taken.</font>");
              }
              }          
           });          
          }        
        });
        
      
      //to show password strength during signup input !!
      var options = {
        minChar: 5,
        bootstrap3: false,
        };
        $("#password").pwstrength(options);
        jQuery(document).ready(function () {  
        "use strict";
        var options = {
        minChar: 8,
        bootstrap3: false,
        errorMessages: {
            password_too_short: "<font color='red'>The Password is too short</font>",
            same_as_username: "Your password cannot be the same as your username"
        },
        scores: [17, 26, 40, 50],
        verdicts: ["Weak", "Normal", "Medium", "Strong", "Very Strong"],
        showVerdicts: true,
        showVerdictsInitially: false,
        raisePower: 1.4,
        usernameField: "#username",
        };
        $("#password").pwstrength(options);
      });
      //to validate the signin form input fields !!
      
      
      $("#act_carousel_vertical").jCarouselLite({
      vertical: true,  
      visible: 5,
      scroll: 1,
      auto: 3000,  
      speed: 1000,
      circular: false  
      });         
      $("#pop_carousel_vertical").jCarouselLite({
      vertical: true,  
      visible: 5,
      scroll: 1,
      auto: 3000,  
      speed: 2000,
      circular: false  
      });
        // Using default configuration
        $('#mycarousel').carouFredSel({
        auto: false,
        circular: false,
        infinite: false,
        prev: '#prev2',
        next: '#next2',
        responsive: true,
        width: '100%',
        scroll: 2,
        items: {
        width: null,
        //	height: '30%',	//	optionally resize item-height
        visible: {
        min: null,
        max: 4
        }
        }
        });
        
       
  });
  
  
  
